import React, { Component } from 'react';
import { Card, CardDeck, CardGroup, CardColumns, Container, Row, Col } from 'react-bootstrap';
import axios from "axios";
import moment from 'moment';
import { usePosition } from 'use-position';

import io from 'socket.io-client';

import './Cards.css';

const key = "9b6e02c485d9161a33a3956639611759";


class Cards extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: null,
            city: "Burbank",
            lat: 34.2418,
            long: -118.5289,
            temperature: undefined,
            location: null
        }
    }

    componentDidMount() { 
        axios.get(`http://api.openweathermap.org/data/2.5/weather?lat=${this.state.lat}&lon=${this.state.long}&APPID=${key}`)
            .then (res => {
                console.log(res);
                const data = res.data;
                this.setState({
                    data: data,
                    temperature: data.main.temp
                });
            })

    };
    // getPosition = (options) => {
    //     return new Promise(function (resolve, reject) {
    //       navigator.geolocation.getCurrentPosition(resolve, reject, options);
    //     });
    // }
    render() { 
        // console.log(`${this.state.lat}`)
        // console.log(`${this.state.long}`)
        console.log(`${this.state.temperature}`)
        // console.log(`${this.state.location}`)
        return (
            <React.Fragment>
            {/* // <div className="card-container">  */}
                <Container>
                <Row className="row justify-content-between">
                    <Col className="col-6">
                        <Card className="weather-card" border="danger" style={{ width: '15rem' }}>
                            <Card.Header>Weather</Card.Header>
                            <Card.Body>
                                <Card.Title>Today's Forecast</Card.Title>
                                    {/* <p>{this.state.data} </p> */}
                            </Card.Body>
                        </Card>  
                    </Col>
                    <Col className="col-6">
                        <Card className="battery" border="danger" style={{ width: '15rem' }}>
                            <Card.Header>Battery</Card.Header>
                            <Card.Body>
                                <Card.Title>Battery Temperature</Card.Title>
                                <Card.Text>
                                    Current Battery Temperature is 30 degrees Fareinheit
                                </Card.Text>
                            </Card.Body>
                        </Card> 
                    </Col> 
                </Row> 
                <Row className="row justify-content-between bottom-row">
                    <Col className="col-6 bottom-col">
                        <Card className="weather-card" border="danger" style={{ width: '15rem' }}>
                            <Card.Header>Speed Statistics</Card.Header>
                            <Card.Body>
                                <Card.Title> </Card.Title>
                                <Card.Text>
                                    Top    :
                                    Average:       
                                </Card.Text>
                            </Card.Body>
                        </Card>  
                    </Col>
                    <Col className="col-6 bottom-col">
                        <Card className="battery" border="danger" style={{ width: '15rem' }}>
                            <Card.Header>Header</Card.Header>
                            <Card.Body>
                                <Card.Title>Danger Card Title</Card.Title>
                                <Card.Text>
                                    Some quick example text to build on the card title and make up the bulk
                                    of the card's content.
                                </Card.Text>
                            </Card.Body>
                        </Card> 
                    </Col> 
                </Row>       
                </Container>    
            {/* </div> */}
            </React.Fragment>
        )
    }

}

export default Cards;
